
public class Ex02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			System.out.println(100);
			System.out.println(10.3);
			System.out.println('a');//단일 따옴표로 묶인 char타입
			System.out.println(true);//true or false를 다루는 논리형 타입=> boolean
			System.out.println(10L);//10뒤에 접미사 l(L)을 붙이면  long 정수 자료형 값이 된다.
			System.out.println(100.3f);//100.3뒤에 접미사 f(F)를 붙이면 실수형
			//4바이트 크기인  float타입이 된다.
			System.out.println("홍길동");//이중 따옴표로 묶어진 String문자열 참조 타입이 된다.
	}

}
